mapper.select:



        and nick_name like concat('%', #{nickName}, '%')


        and phone = #{phone}


        and student_no = #{studentNo}


        and auth_status = #{authStatus}


        and status = #{status}


        and date_format(create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')


        and date_format(create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')


order by create_time desc

mapper.update:
update mini_user
open_id = #{openId}, union_id = #{unionId}, nick_name = #{nickName}, avatar_url = #{avatarUrl}, phone = #{phone}, student_no = #{studentNo}, real_name = #{realName}, gender = #{gender}, status = #{status}, auth_status = #{authStatus}, auth_fail_reason = #{authFailReason}, review_by = #{reviewBy}, review_time = #{reviewTime}, admin_remark = #{adminRemark}, update_time = now()
where id = #{id}

update mini_user
auth_status = #{authStatus}, auth_fail_reason = #{authFailReason}, review_by = #{reviewBy}, review_time = #{reviewTime}, update_time = now()
where id = #{id}
